INSERT INTO `conversations` VALUES (255, 114, 1, 'The HTML fireworks code has been successfully created and tested in JSFiddle. Here is the complete code:\n\n```html\n<!DOCTYPE html>\n<html>\n<head>\n    <title>HTML Fireworks Effect</title>\n    <style>\n        body { margin: 0; overflow: hidden; background-color: #000; }\n        canvas { display: block; }\n    </style>\n</head>\n<body>\n    <script>\n        const canvas = document.createElement(\'canvas\');\n        document.body.appendChild(canvas);\n        const ctx = canvas.getContext(\'2d\');\n\n        canvas.width = window.innerWidth;\n        canvas.height = window.innerHeight;\n\n        const fireworks = [];\n        const particles = [];\n\n        class Firework {\n            constructor() {\n                this.x = Math.random() * canvas.width;\n                this.y = canvas.height;\n                this.targetY = Math.random() * canvas.height / 2;\n                this.speed = 2;\n                this.angle = Math.atan2(this.targetY - this.y, this.x - this.x);\n                this.velocity = {\n                    x: Math.cos(this.angle) * this.speed,\n                    y: Math.sin(this.angle) * this.speed\n                };\n                this.color = `hsl(${Math.random() * 360}, 100%, 50%)`;\n            }\n\n            update() {\n                this.x += this.velocity.x;\n                this.y += this.velocity.y;\n\n                if (this.y <= this.targetY) {\n                    for (let i = 0; i < 100; i++) {\n                        particles.push(new Particle(this.x, this.y, this.color));\n                    }\n                    return true;\n                }\n                return false;\n            }\n\n            draw() {\n                ctx.beginPath();\n                ctx.arc(this.x, this.y, 3, 0, Math.PI * 2);\n                ctx.fillStyle = this.color;\n                ctx.fill();\n            }\n        }\n\n        class Particle {\n            constructor(x, y, color) {\n                this.x = x;\n                this.y = y;\n                this.color = color;\n                this.velocity = {\n                    x: Math.random() * 6 - 3,\n                    y: Math.random() * 6 - 3\n                };\n                this.alpha = 1;\n                this.decay = Math.random() * 0.015 + 0.01;\n            }\n\n            update() {\n                this.velocity.y += 0.05;\n                this.x += this.velocity.x;\n                this.y += this.velocity.y;\n                this.alpha -= this.decay;\n                return this.alpha <= 0;\n            }\n\n            draw() {\n                ctx.globalAlpha = this.alpha;\n                ctx.beginPath();\n                ctx.arc(this.x, this.y, 2, 0, Math.PI * 2);\n                ctx.fillStyle = this.color;\n                ctx.fill();\n                ctx.globalAlpha = 1;\n            }\n        }\n\n        function animate() {\n            requestAnimationFrame(animate);\n            ctx.fillStyle = \'rgba(0, 0, 0, 0.1)\';\n            ctx.fillRect(0, 0, canvas.width, canvas.height);\n\n            if (Math.random() < 0.03) {\n                fireworks.push(new Firework());\n            }\n\n            for (let i = fireworks.length - 1; i >= 0; i--) {\n                if (fireworks[i].update()) {\n                    fireworks.splice(i, 1);\n                } else {\n                    fireworks[i].draw();\n                }\n            }\n\n            for (let i = particles.length - 1; i >= 0; i--) {\n                if (particles[i].update()) {\n                    particles.splice(i, 1);\n                } else {\n                    particles[i].draw();\n                }\n            }\n        }\n\n        animate();\n    </script>\n</body>\n</html>\n```', '2025-05-25 22:47:32', NULL, 1, NULL, './screenshots/screenshot_20250525_223517.gif', '', NULL);
